interface Person extends Animal {
  work():void;
}
// 有个类 实现 这个接口
class Web implements Person {
  public name:string;
  constructor(name:string){
    this.name=name
  }
  eat(){
    console.log(this.name + '喜欢吃馒头')
  }
  work(){
    console.log(this.name + '写代码')
  }
}

