```js
const {
  //同步串行的钩子， 不用关心函数的返回值
  SyncHook,
  // 同步串行的钩子 ， 上一个返回值不为null 跳过剩下的逻辑
  SyncBailHook,
  // 同步串行， 上一个值可以传递给下一个值
  SyncWaterfallHook,
  SyncLoopHook,
  AsyncParallelHook,
  AsyncParallelBailHook,
  AsyncSeriesHook,
  AsyncSeriesBailHook,
  AsyncSeriesWaterfallHook 
