  name:string;
  constructor(name:string){
    this.name = name;
  }
  eat():void{
    console.log(`${this.name}， 在吃屎`)
  }
}

class Dog extends Animal {
  constructor(name:string){
    super(name)
  }
  // 子类重新定义 eat方法
  eat():void {
    console.log(this.name+'吃肉')
  }
}

const wuxiaohong = new Dog('吴晓红')
wuxiaohong.eat()

// 子类重新定义 eat方法
class Cat extends Animal {
  constructor(name:string) {
    super(name)
  }
  eat():void{
    console.log(this.name+ '吃粮食')
  }
}

var cc = new Cat('吴强')
cc.eat()
```

## 抽象类
